INSERT INTO `Template` VALUES (15,'Devis','pdf','2015-01-15 14:58:52','Devis - {{inscription.stagiaire.nom}} - {{inscription.stagiaire.prenom}}','<table style=\"height: 100%; width: 100%;\">\r\n<tbody>\r\n<tr>\r\n<td style=\"vertical-align: top;\"><span style=\"font-size: 10pt;\"><img src=\"http://www.ocim.fr/wp-content/themes/ocim/img/bandeau-lateral-courrier-2017.png\" alt=\"Bandeau-Ocim\" width=\"185\" height=\"1080\" /></span></td>\r\n<td style=\"vertical-align: top;\">\r\n<div style=\"vertical-align: top; text-align: right;\">&nbsp;</div>\r\n<div style=\"vertical-align: top; text-align: right;\">&nbsp;</div>\r\n<div style=\"vertical-align: top; text-align: right;\">&nbsp;</div>\r\n<div style=\"vertical-align: top; text-align: right;\"><span style=\"font-size: 10pt;\">Dijon, le {{date}} &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></div>\r\n<div style=\"vertical-align: top; text-align: right;\"><span style=\"font-size: 10pt;\">&nbsp;</span></div>\r\n<div style=\"float: right; margin-right: 2cm; border: 1pt solid grey; width: 8cm; padding: 0.15cm;\">{{ inscription.signataire.adresse.nomStructure }}<br /><span style=\"font-size: 10pt;\"> {{ inscription.signataire.adresse.adresse }}</span><br /><span style=\"font-size: 10pt;\"> {{ inscription.signataire.adresse.adresseComplement }}</span><br /><span style=\"font-size: 10pt;\"> {{ inscription.signataire.adresse.CP }} {{ inscription.signataire.adresse.ville }} {{ inscription.signataire.adresse.pays }}</span></div>\r\n<h2 style=\"text-align: center;\">&nbsp;</h2>\r\n<h2 style=\"text-align: center;\">&nbsp;</h2>\r\n<h2 style=\"text-align: center;\">&nbsp;</h2>\r\n<h2 style=\"text-align: center;\">&nbsp;</h2>\r\n<h2 style=\"text-align: center;\"><span style=\"font-size: 10pt;\"><br />DEVIS DE FORMATION</span></h2>\r\n<p style=\"text-align: left;\"><span style=\"font-size: 10pt;\"><br />Parti